mapper.select:
   	select c.rule_publish_id,c.rule_title,c.rule_type,c.user_type_id,c.region_id,c.role_id,c.rule_content,c.rule_datetime_id,c.create_time,c.modify_time,c.is_alive,c.is_del,c.publish_user_id,s.id,s.rule_datetime_id,s.rule_datetime_start,s.rule_datetime_end,s.rule_money,s.order_num,s.rule_num
	 from yqdb.rule_publish c,yqdb.rule_publish_datetime s
	 where c.rule_datetime_id=s.rule_datetime_id
	 and c.publish_user_id=#{publishUserId}
	 and c.rule_publish_id=#{rulePublishId}
